| image1 = Pseudotsuga menziesii Tiger Mountain Seattle.jpg
| width1 = {{#expr: (120 * 2217 / 2637) round 0}}
| caption1 =  ''Pseudotsuga menziesii''
| image2 = Pseudotsuga menziesii levila.png
| width2 = {{#expr: (120 * 1100/ 900) round 0}}
